import React, { useEffect, useState, useContext } from "react";
import app from "../config/firebase";

const [currentUser, setCurrentUser] = useState(null);

useEffect(() => {
  app.auth().onAuthStateChanged(setCurrentUser);
}, []);

export const useAuth = () => {
  return currentUser;
};
