import React, { useState } from "react";
import { Form, Checkbox } from "semantic-ui-react";
import styles from "../styles/Home.module.css";
import { quiz } from "../content/quiz";

export default function Quiz() {
  const [value, setValue] = useState("");
  const [currentIndex, setCurrentIndex] = useState(0);
  const [done, setDone] = useState(false);

  const handleChange = (e, { value }) => {
    setValue(value);
  };

  const handleNext = () => {
    const next = quiz[currentIndex + 1];
    if (next) {
      setCurrentIndex(currentIndex + 1);
    } else {
      setDone(true);
    }
  };

  const current = quiz[currentIndex];
  const percentDone = Math.round(((currentIndex + 1) / quiz.length) * 100);
  return (
    <div className="more-info-modal">
      <h1 className={styles.fontandcenter}>Quiz</h1>
      <div className="progress topspace">
        <div
          className="progress-bar"
          role="progressbar"
          style={{ width: `${percentDone}%` }}
          aria-valuenow={percentDone}
          aria-valuemin=""
          aria-valuemax="100"
        >
          {percentDone}%
        </div>
      </div>
      <Form className="topspace">
        {current ? (
          <>
            <Form.Field>
              <strong>{current.question}</strong>
            </Form.Field>
            {current.answers.map((answer) => (
              <Form.Field>
                <Checkbox
                  key={answer.value}
                  radio
                  label={answer.txt}
                  name="checkboxRadioGroup"
                  value={answer.value}
                  checked={value === answer.value}
                  onChange={handleChange}
                />
              </Form.Field>
            ))}
          </>
        ) : null}
        <Form.Field>
          <button
            onClick={handleNext}
            type="button"
            className="btn btn-success center-item topspace"
          >
            Next
          </button>
        </Form.Field>
      </Form>
    </div>
  );
}
