import React, { useEffect, useState } from "react";
import app from "../config/firebase";

export function useAuth() {
  // const [currentUser, setCurrentUser] = useState(null);
  let currentUser = useEffect(() => {
    app.auth().onAuthStateChanged((user) => {
      currentUser = user;
    });
  }, []);

  return currentUser;
}
