import React, { useEffect, useState, useContext } from "react";
import app from "../config/firebase";

export function useAuth() {
  const [currentUser, setCurrentUser] = useState(null);

  useEffect(() => {
    app.auth().onAuthStateChanged(setCurrentUser);
  }, []);

  return currentUser;
}
