import React, { useState, useEffect } from "react";
import { useRouter } from "next/router";
import { useForm } from "react-hook-form";
import NavBar from "../components/Navbar";
import Metadata from "../components/Metadata";
import styles from "../styles/Home.module.css";
import { app } from "../config/firebase";
import { useAuth } from "../hooks/useAuth";

export default function login() {
  const [user, setUser] = useState(undefined);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [noEmail, setNoEmail] = useState(false);
  const [noPassword, setNoPassword] = useState(false);
  const [noConfirmPassword, setNoConfirmPassword] = useState(false);
  const [createAccount, setCreateAccount] = useState(false);
  const router = useRouter();
  const auth = useAuth();
  const { register, handleSubmit, watch, errors } = useForm();
  const onSubmitLogin = async () => {
    auth.signIn(email, password);
    setNoPassword(false);
    setNoEmail(false);
  };

  const onSubmitSignup = async () => {
    auth.signIn(email, password);
    setNoPassword(false);
    setNoEmail(false);
  };

  useEffect(() => {
    app.auth().onAuthStateChanged(function (user) {
      if (user) {
        setUser(user);
        console.log(user);
      }
    });
  }, []);

  if (user) {
    router.push("/");
  }
  return (
    <div className={styles.container}>
      <Metadata title="Xmas Throwdown Login" />
      <NavBar />
      <main className={styles.main}>
        <button
          onClick={() => setCreateAccount(!createAccount)}
          type="button"
          className="btn btn-dark"
        >
          {createAccount
            ? "Have an account? Click here to login"
            : "Don't have an account? Click here to create one!"}
        </button>
        {!createAccount ? (
          <form
            className={styles.margintop}
            onSubmit={handleSubmit(onSubmitLogin)}
          >
            <div className="form-group">
              <label htmlFor="exampleInputEmail1">Email address</label>
              <input
                name="email"
                value={email}
                onChange={(e) => {
                  if (e.target.value === "") {
                    setNoEmail(true);
                    setEmail(e.target.value);
                  } else {
                    setNoEmail(false);
                    setEmail(e.target.value);
                  }
                }}
                type="email"
                className={noEmail ? "form-control is-invalid" : "form-control"}
                id="exampleInputEmail1"
                aria-describedby="emailHelp"
                ref={register({
                  required: "Please enter an email",
                })}
              />
              <div id="validationServer03Feedback" className="invalid-feedback">
                Please provide an email.
              </div>
              <small id="emailHelp" className="form-text text-muted">
                If you have a wod-with-faris account you can login using that.
              </small>
            </div>
            <div className="form-group">
              <label htmlFor="exampleInputPassword1">Password</label>
              <input
                name="password"
                value={password}
                onChange={(e) => {
                  if (e.target.value === "") {
                    setNoPassword(true);
                    setPassword(e.target.value);
                  } else {
                    setNoPassword(false);
                    setPassword(e.target.value);
                  }
                }}
                type="password"
                className={
                  noPassword ? "form-control is-invalid" : "form-control"
                }
                id="exampleInputPassword1"
              />
              <div id="validationServer03Feedback" className="invalid-feedback">
                Please provide a password.
              </div>
            </div>
            <div className="form-group form-check"></div>
            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </form>
        ) : (
          <form
            className={styles.margintop}
            onSubmit={handleSubmit(onSubmitSignup)}
          >
            <div className="form-group">
              <label htmlFor="exampleInputEmail1">First Name</label>
              <input
                name="first-name"
                value={firstName}
                onChange={(e) => {
                  if (e.target.value === "") {
                    setNoFirstName(true);
                    setFirstName(e.target.value);
                  } else {
                    setNoFirstName(false);
                    setFirstName(e.target.value);
                  }
                }}
                type="text"
                className={
                  noFirstName ? "form-control is-invalid" : "form-control"
                }
                id="exampleInputEmail1"
                aria-describedby="emailHelp"
              />
              <label htmlFor="exampleInputEmail1">Last Name</label>
              <input
                name="last-name"
                value={lastName}
                onChange={(e) => {
                  if (e.target.value === "") {
                    setNoLastName(true);
                    setLastName(e.target.value);
                  } else {
                    setNoLastName(false);
                    setLastName(e.target.value);
                  }
                }}
                type="text"
                className={
                  noLastName ? "form-control is-invalid" : "form-control"
                }
                id="exampleInputEmail1"
                aria-describedby="emailHelp"
              />
              <label htmlFor="exampleInputEmail1">Email address</label>
              <input
                name="email"
                value={email}
                onChange={(e) => {
                  if (e.target.value === "") {
                    setNoEmail(true);
                    setEmail(e.target.value);
                  } else {
                    setNoEmail(false);
                    setEmail(e.target.value);
                  }
                }}
                type="email"
                className={noEmail ? "form-control is-invalid" : "form-control"}
                id="exampleInputEmail1"
                aria-describedby="emailHelp"
              />
              <div id="validationServer03Feedback" className="invalid-feedback">
                Please provide an email.
              </div>
              <small id="emailHelp" className="form-text text-muted">
                If you have a wod-with-faris account you can login using that.
              </small>
            </div>
            <div className="form-group">
              <label htmlFor="exampleInputPassword1">Password</label>
              <input
                name="password"
                value={password}
                onChange={(e) => {
                  if (e.target.value === "") {
                    setNoPassword(true);
                    setPassword(e.target.value);
                  } else {
                    setNoPassword(false);
                    setPassword(e.target.value);
                  }
                }}
                type="password"
                className={
                  noPassword ? "form-control is-invalid" : "form-control"
                }
                id="exampleInputPassword1"
                ref={register({
                  required: "Please enter a password",
                  minLength: {
                    value: 6,
                    message: "Should have at least 6 characters",
                  },
                })}
              />
              <div id="validationServer03Feedback" className="invalid-feedback">
                Please provide a password.
              </div>
              <label htmlFor="exampleInputPassword1">Confirm Password</label>
              <input
                name="confirm-password"
                value={confirmPassword}
                onChange={(e) => {
                  if (e.target.value === "") {
                    setNoConfirmPassword(true);
                    setConfirmPassword(e.target.value);
                  } else {
                    setNoConfirmPassword(false);
                    setConfirmPassword(e.target.value);
                  }
                }}
                type="password"
                className={
                  noConfirmPassword ? "form-control is-invalid" : "form-control"
                }
                id="exampleInputPassword1"
                ref={register({
                  required: "Please enter a password",
                  minLength: {
                    value: 6,
                    message: "Should have at least 6 characters",
                  },
                })}
              />
              <div id="validationServer03Feedback" className="invalid-feedback">
                Please confirm password.
              </div>
            </div>
            <div className="form-group form-check"></div>
            <button type="submit" className="btn btn-primary">
              Create Account
            </button>
          </form>
        )}
      </main>
    </div>
  );
}
