import React, { useState } from "react";
import PopPop from "react-poppop";

export default function FAQ(props) {
  const [showModal, setShowModal] = useState(false);

  const { FAQs } = props;

  const toggleShow = (show) => {
    setShowModal(show);
  };

  return (
    <div>
      <div className="accordion" id="accordionExample">
        {FAQs.map((FAQ) => (
          <div key={FAQ.question} className="card">
            <div className="card-header" id="headingOne">
              <h2 className="mb-0">
                <button
                  className="btn btn-link btn-block text-left"
                  type="button"
                  data-toggle="collapse"
                  data-target="#collapseOne"
                  aria-expanded="false"
                  aria-controls="collapseOne"
                >
                  {FAQ.question}
                </button>
              </h2>
            </div>

            <div
              id="collapseOne"
              className="collapse"
              aria-labelledby="headingOne"
              data-parent="#accordionExample"
            >
              <div className="card-body">
                {FAQ.answer}
                {FAQ.button === "quiz" && (
                  <div>
                    <button className="btn btn-success" onClick={null}>
                      Quiz
                    </button>
                    <PopPop
                      position="centerCenter"
                      open={showModal}
                      closeBtn={true}
                      closeOnEsc={true}
                      onClose={() => toggleShow(false)}
                      closeOnOverlay={true}
                    >
                      <div className="more-info-modal">
                        <h1 className={styles.fontandcenter}>{title}</h1>
                        <h5>Standards:</h5>
                        <small
                          id="standardsHelp"
                          className="form-text text-muted"
                        >
                          At least 2 team members should fit this criteria
                        </small>
                        <ul className="list-group">
                          {requirements.map((requirement) => (
                            <li key={requirement} className="list-group-item">
                              {requirement}
                            </li>
                          ))}
                        </ul>
                      </div>
                    </PopPop>
                  </div>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
